package lsieun;import lsieun.cv.*;import lsieun.utils.FileUtils;import org.objectweb.asm.*;public class HelloWorldModifyMethod {    public static void main(String[] args) {        String relativePath = "sample/HelloWorld.class";        String filePath = FileUtils.getFilePath(relativePath);        byte[] bytes = FileUtils.readBytes(filePath);        ClassReader cr = new ClassReader(bytes);        ClassWriter cw = new ClassWriter(ClassWriter.COMPUTE_FRAMES);        int api = Opcodes.ASM9;        ClassVisitor cv = new MethodEnterVisitor(api, cw);        ClassVisitor cv2 = new MethodExitVisitor(api, cv);        int parseOptions = ClassReader.SKIP_DEBUG | ClassReader.SKIP_FRAMES;        cr.accept(cv2, parseOptions);        byte[] bytes1 = cw.toByteArray();        FileUtils.writeBytes(filePath, bytes1);    }}