package lsieun.cv;import org.objectweb.asm.ClassVisitor;import org.objectweb.asm.Opcodes;/** * 修改类接口 * */public class ClassChangeInterfaceVisitor extends ClassVisitor {    public ClassChangeInterfaceVisitor(int api, ClassVisitor classVisitor) {        super(api, classVisitor);    }    @Override    public void visit(int version, int access, String name, String signature, String superName, String[] interfaces) {        super.visit(version, access, name, signature, superName, new String[]{"java/lang/Cloneable"});    }}