package lsieun.cv;import org.objectweb.asm.ClassVisitor;import org.objectweb.asm.Opcodes;/** * 切换类版本 * */public class ClassChangeVersionVisitor extends ClassVisitor {    public ClassChangeVersionVisitor(int api, ClassVisitor classVisitor) {        super(api, classVisitor);    }    @Override    public void visit(int version, int access, String name, String signature, String superName, String[] interfaces) {        super.visit(Opcodes.V1_6, access, name, signature, superName, interfaces);    }}