package lsieun.cv;import org.objectweb.asm.ClassVisitor;import org.objectweb.asm.MethodVisitor;import org.objectweb.asm.Opcodes;public class MethodAroundVisitor extends ClassVisitor {    public MethodAroundVisitor(int api, ClassVisitor classVisitor) {        super(api, classVisitor);    }    @Override    public MethodVisitor visitMethod(int access, String name, String descriptor, String signature, String[] exceptions) {        MethodVisitor mv = super.visitMethod(access, name, descriptor, signature, exceptions);        if (mv != null && !"<init>".equals(name)) {            mv = new MethodAroundAdapter(api, mv);        }        return mv;    }    private static class MethodAroundAdapter extends MethodVisitor {        public MethodAroundAdapter(int api, MethodVisitor methodVisitor) {            super(api, methodVisitor);        }        @Override        public void visitCode() {            super.visitFieldInsn(Opcodes.GETSTATIC, "java/lang/System", "out", "Ljava/lang/PrintStream;");            super.visitLdcInsn("method enter");            super.visitMethodInsn(Opcodes.INVOKESPECIAL, "java/lang/PrintStream", "println", "(Ljava/lang/String)V", false);            super.visitCode();        }        @Override        public void visitInsn(int opcode) {            if (Opcodes.IRETURN <= opcode && Opcodes.RETURN >= opcode || Opcodes.ATHROW == opcode) {                super.visitFieldInsn(Opcodes.GETSTATIC, "java/lang/System", "out", "Ljava/lang/PrintStream;");                super.visitLdcInsn("method exit");                super.visitMethodInsn(Opcodes.INVOKESPECIAL, "java/lang/PrintStream", "println", "(Ljava/lang/String)V", false);            }            super.visitInsn(opcode);        }    }}