package lsieun.cv;import lsieun.opcode.Opcode;import org.objectweb.asm.ClassVisitor;import org.objectweb.asm.MethodVisitor;import org.objectweb.asm.Opcodes;public class MethodExitVisitor extends ClassVisitor {    public MethodExitVisitor(int api, ClassVisitor classVisitor) {        super(api, classVisitor);    }    @Override    public MethodVisitor visitMethod(int access, String name, String descriptor, String signature, String[] exceptions) {        MethodVisitor mv = super.visitMethod(access, name, descriptor, signature, exceptions);        if (mv != null && !"<init>".equals(name)) {            mv = new MethodExitAdapter(api, mv);        }        return mv;    }    private static class MethodExitAdapter extends MethodVisitor {        public MethodExitAdapter(int api, MethodVisitor methodVisitor) {            super(api, methodVisitor);        }        @Override        public void visitVarInsn(int opcode, int varIndex) {            if (Opcodes.IRETURN == opcode || Opcodes.ATHROW == opcode) {                super.visitFieldInsn(Opcodes.GETSTATIC, "java/lang/System", "out", "Ljava/lang/PrintStream;");                super.visitLdcInsn("method exit");                super.visitMethodInsn(Opcodes.INVOKESPECIAL, "java/lang/PrintStream", "println", "(Ljava/lang/String)V", false);            }            super.visitVarInsn(opcode, varIndex);        }    }}